        private void buttonAddOrder_Click(object sender, EventArgs e)
        {
            List<OrderBook> list2 = OrdeerIO.Listorders();
            if ((ValidatorOrder.IsValidName(textBoxClientFirst)) && (ValidatorOrder.IsValidName(textBoxClientLast)) &&(ValidatorOrder.IsvalidQty(Convert.ToInt32(textBoxOrderQty.Text))) && (ValidatorOrder.IsValidName(textBoxOrderBookNmae)) && (ValidatorOrder.IsValidName(textBoxOrderBookNmae)) && ValidatorOrder.IsUniqueID(list2,Convert.ToInt32(textBoxOrderID.Text)))



            {

                OrderBook odr = new OrderBook();

                odr.firstName = textBoxClientFirst.Text;
                odr.lastName = textBoxClientLast.Text;
                odr.orderId = Convert.ToInt32(textBoxOrderID.Text);
                odr.phoneNumber = maskedTextBoxClientPhone.Text;
                odr.quantity = Convert.ToInt32(textBoxOrderQty.Text);
                odr.payableAmount = Convert.ToInt32(textBoxOrderamount.Text);
                odr.bookName = textBoxOrderBookNmae.Text;
                OrdeerIO.Add(odr);
                ClearAll();

            }
        }

